package datainsider.jobworker.service.handlers

import com.twitter.inject.Test
import com.twitter.util.Await
import datainsider.client.domain.schema.TableSchema
import datainsider.client.domain.schema.column.{DateColumn, FloatColumn, Int32Column, Int64Column, StringColumn}
import datainsider.jobworker.domain.{JobStatus, MongoJob, MongoSource, TLSConfiguration}
import datainsider.jobworker.service.handler.SourceMetadataHandler
import datainsider.jobworker.util.ZConfig

class MongoHandlerTest extends Test {

  val host: String = ZConfig.getString("database_test.mongodb.host")
  val username: String = ZConfig.getString("database_test.mongodb.username")
  val password: String = ZConfig.getString("database_test.mongodb.password")

  val dataSource: MongoSource = MongoSource(
    orgId = 1L,
    id = 1,
    displayName = "test",
    host = host,
    port = None,
    username = username,
    password = password,
    tlsConfiguration = None,
    connectionUri = None
  )
  val sourceHandler: SourceMetadataHandler = SourceMetadataHandler(dataSource)

  test("mongodb test connection") {
    val connected = Await.result(sourceHandler.testConnection())
    assert(connected)
  }

  test("mongodb list db") {
    val dbs: Seq[String] = Await.result(sourceHandler.listDatabases())
    assert(dbs.nonEmpty)
    assert(dbs.contains("admin"))
    println(dbs.mkString(", "))
  }

  test("mongodb list tbl") {
    val tables: Seq[String] = Await.result(sourceHandler.listTables("highschool"))
    assert(tables.nonEmpty)
    assert(tables.contains("student"))
    println(tables.mkString(", "))
  }

  test("mongodb suggest table schema") {
    val job: MongoJob = MongoJob(
      1,
      jobId = 2,
      sourceId = 0L,
      lastSuccessfulSync = 0L,
      syncIntervalInMn = 0,
      lastSyncStatus = JobStatus.Init,
      currentSyncStatus = JobStatus.Queued,
      displayName = "test",
      destDatabaseName = "test",
      destTableName = "test",
      databaseName = "highschool",
      tableName = "student",
      incrementalColumn = None,
      lastSyncedValue = "0",
      maxFetchSize = 1000,
      flattenDepth = 0
    )
    val connected = Await.result(sourceHandler.testJob(job))
    assert(connected)
  }

